func (wis *WorkloadItemSelect) sqlScan(ctx context.Context, v interface{}) error {
	rows := &sql.Rows{}
	query, args := wis.sqlQuery().Query()
	if err := wis.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

func (wis *WorkloadItemSelect) sqlQuery() sql.Querier {
	selector := wis.sql
	selector.Select(selector.Columns(wis.fields...)...)
	return selector
}
